var expect = require('expect.js');
var runTask = require('grunt-run-task');

describe('test', function(){
    it('test', function(done){
        var task = runTask.task('test', {
        });

        task.run(function(err){
            if (err) {
                done(err);
            }
            expect(true).to.be(true);
            done();
        });
    });
});
